package uz.lutfullayevmuhammad.lesson69import android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport androidx.core.content.ContextCompatimport uz.lutfullayevmuhammad.lesson69.service.MyForegroundServiceclass RebootBroadcast : BroadcastReceiver() {    override fun onReceive(context: Context?, intent: Intent?) {        intent?.let {            val intent1 = Intent(context, MyForegroundService::class.java)            intent1.putExtra("state", true)            if (context != null) {                ContextCompat.startForegroundService(context, intent1)            }        }    }}