package uz.lutfullayevmuhammad.lesson69.serviceimport android.app.Serviceimport android.content.Intentimport android.os.IBinderimport android.util.Logclass MyBackgroundService : Service() {    private lateinit var thread: Thread    private val TAG = "MyBackgroundServiceTAG"    override fun onBind(intent: Intent?): IBinder? {        return null    }    override fun onStartCommand(intent: Intent?, flags: Int, startId: Int): Int {        if (intent!!.getBooleanExtra("state", false)) {           this.thread= Thread {                for (i in 0 until 10) {                    Log.d(TAG, "onStartCommand: ")                    Thread.sleep(1_000)                }            }               thread.start()        }else{            stopSelf()        }        return super.onStartCommand(intent, flags, startId)    }}